mapper.select:
select id, name, description, img_url, sort, is_display
from cy_brand

	is_deleted = 'N'
	
		and name like "%"#{name}"%"
	
	
		and is_display = #{isDisplay}
	


	limit #{start}, #{limit}

select id, name, description, img_url, sort, is_display
from cy_brand

	id = #{id}

mapper.update:
update cy_brand

	update_time = now(),
	
		name = #{name},
	
	
		description = #{description},
	
	
		img_url = #{imgUrl},
	
	
		sort = #{sort},
	
	
		is_display = #{isDisplay}
	
	
		update_time = #{updateTime}
	


	id = #{id}

update cy_brand set is_deleted = 'Y', update_time = now()

	id in
	
		#{id}
